import boto3
import json
import base64
import time


s3_client = boto3.client('s3')

def lambda_handler(event, context):

    bucketName = 'voidawsbucket' #define bucket nam3e

    try:
        request_body = json.loads(event['body']) #request body
        #Validation for request
        required_fields = ['image'] # you can add more fields which are required
        missing_fields = [field for field in required_fields if field not in request_body]   
        if missing_fields:
            return {
                'statusCode': 400,
                'body':json.dumps({'message': f'The following fields are missing: {", ".join(missing_fields)}'})
            }
            
        base64_data = request_body.get('image', '') #image from request
        uploaderId = request_body.get('uploaderId', '') #uploaderId from request
        current_timestamp = int(time.time())
        formattedBase64 = base64_data.split(',')[1]
        extension = base64_data.split(',')[0].split('/')[-1].split(';')[0]
        imageData = base64.b64decode(formattedBase64)
        fileName = str(current_timestamp)+"-"+uploaderId+'.'+extension
        filePath = "uploaded-images/"+fileName
        s3_client.put_object(Bucket=bucketName, Key=filePath, Body=imageData)
        
        dynamodb = boto3.resource('dynamodb')
        dataToDynamoDB = {
        'fileName': fileName,
        'filePath': filePath,
        'uploadedOn': current_timestamp,
        }
        dynamo_table = dynamodb.Table('uploaded-images')  # define the table name
        dynamo_table.put_item(Item=dataToDynamoDB)
        return {
            'statusCode': 200,
            'body': json.dumps({"message": 'success'})
        }
    except Exception as e:
        return {
            'statusCode': 500,
            'body': json.dumps({'message': e})
        }
    


